mapper.insert:

 				SELECT emp_seq.NEXTVAL 
 				  FROM DUAL

			INSERT INTO BOARD
							 VALUES(
				  				     #{num}
			                        ,#{title}
			                        ,#{contents}
			                        ,#{writer}
			                        ,SYSDATE
			                         )

mapper.update:
UPDATE BOARD SET 
			      TITLE = #{title}
				, CONTENTS = #{contents}
				, WRITER = #{writer}
WHERE NUM = #{num}

mapper.select:
     SELECT 
		   CALNO,
		   CALTIME,
		   CALREQ,
		   CALCONTENTS
	 FROM  CALENDAR
	 WHERE CALYEAR = #{calYear}
	   AND CALMONTH = #{calMonth}
	   AND CALDAY = #{calDay}
